import React, { useState } from 'react';
import axios from 'axios';

const ItineraryCard = ({ itinerary, userId }) => {
  const [likes, setLikes] = useState(itinerary.likes || 0);
  const [comments, setComments] = useState(itinerary.comments || []);
  const [newComment, setNewComment] = useState('');

  const handleLike = async () => {
    try {
      await axios.post(
        `http://localhost:5000/api/itineraries/${itinerary._id}/like`,
        {},
        { headers: { Authorization: `Bearer ${localStorage.getItem('token')}` } }
      );
      setLikes(likes + 1);
    } catch (error) {
      console.error('Error liking itinerary:', error);
    }
  };

  const handleComment = async (e) => {
    e.preventDefault();
    try {
      await axios.post(
        `http://localhost:5000/api/itineraries/${itinerary._id}/comment`,
        { text: newComment },
        { headers: { Authorization: `Bearer ${localStorage.getItem('token')}` } }
      );
      setComments([...comments, { userId, text: newComment, timestamp: new Date().toISOString() }]);
      setNewComment('');
    } catch (error) {
      console.error('Error adding comment:', error);
    }
  };

  const handleJoin = async () => {
    try {
      await axios.post(
        `http://localhost:5000/api/itineraries/${itinerary._id}/join`,
        {},
        { headers: { Authorization: `Bearer ${localStorage.getItem('token')}` } }
      );
      alert('Join request sent!');
    } catch (error) {
      console.error('Error sending join request:', error);
    }
  };

  return (
    <div className="max-w-md mx-auto p-4 bg-white shadow-md rounded-lg mb-4">
      <h3 className="text-lg font-semibold">{itinerary.title}</h3>
      <p className="text-gray-600">Destination: {itinerary.destination}</p>
      <p className="text-gray-600">Dates: {itinerary.dates}</p>
      <p className="text-gray-600">Participants: {itinerary.participants?.length || 0}/{itinerary.participantLimit}</p>
      <p className="text-gray-600">Vibe: {itinerary.vibeTags.join(', ')}</p>
      <p className="text-gray-600">Budget: {itinerary.budget || 'N/A'}</p>
      <p className="mt-2">{itinerary.description}</p>
      <div className="mt-4 flex gap-4">
        <button
          onClick={handleLike}
          className="bg-green-500 text-white px-3 py-1 rounded hover:bg-green-600"
        >
          Like ({likes})
        </button>
        <button
          onClick={handleJoin}
          className="bg-blue-500 text-white px-3 py-1 rounded hover:bg-blue-600"
          disabled={itinerary.participants?.length >= itinerary.participantLimit}
        >
          Join
        </button>
      </div>
      <div className="mt-4">
        <h4 className="text-sm font-medium">Comments</h4>
        {comments.map((comment, index) => (
          <p key={index} className="text-sm text-gray-600">
            {comment.text} <span className="text-xs">({new Date(comment.timestamp).toLocaleString()})</span>
          </p>
        ))}
        <form onSubmit={handleComment} className="mt-2">
          <input
            type="text"
            value={newComment}
            onChange={(e) => setNewComment(e.target.value)}
            className="p-2 w-full border rounded-md"
            placeholder="Add a comment"
          />
          <button type="submit" className="mt-2 bg-gray-500 text-white px-3 py-1 rounded hover:bg-gray-600">
            Comment
          </button>
        </form>
      </div>
    </div>
  );
};

export default ItineraryCard;
